INSERT INTO RAUCU values (null,"Chuối Cavendish","trái cây",1,10000,"Chuối Cavendish là các giống cây trồng được biết đến là phân nhóm Cavendish, thuộc nhóm chuối trồng AAA của họ chuối.",0,CURRENT_TIMESTAMP,0,20);
