INSERT INTO RAUCU values (null,"Hẹ tây","rau",1,5500,"Allium ascalonicum là một loài thực vật có hoa trong họ Amaryllidaceae.",0,CURRENT_TIMESTAMP,0,22);
INSERT INTO RAUCU values (null,"Cà rốt Đà Lạt","củ",1,5000,"Cà rốt Đà Lạt là một loại củ rất quen thuộc trong các món ăn của người Việt. Cà rốt có hàm lượng chất dinh dưỡng và vitamin A cao, được xem là nguyên liệu cần thiết cho các món ăn dặm của trẻ nhỏ, giúp trẻ sáng mắt và cung cấp nguồn chất xơ dồi dào.",0,CURRENT_TIMESTAMP,0,21);
INSERT INTO RAUCU values (null,"Su hào","rau",1,5000,"Su hào được trồng tại Lâm Đồng là một loại củ rất quen thuộc, được nhiều bà nội trợ chọn để chế biến thành các món ăn ngon cho gia đình. Xu hào rất giàu Vitamin, chất xơ, giúp chống oxy hóa mạnh, răng và nướu khỏe mạnh. Xu hào có thể chế biến thành các món ăn ngon như: xào, nấu canh, muối chua,...",0,CURRENT_TIMESTAMP,0,43);
INSERT INTO RAUCU values (null,"Cam Ai Cập","trái cây",1,35000,"Cam nhập khẩu 100% từ Ai Cập (giấy chứng nhận nguồn gốc xuất xứ). Đạt tiêu chuẩn xuất khẩu toàn cầu. Bảo quản trái cây tươi ngon đến tận tay khách hàng. Cam vàng có trái to tròn, màu vàng tươi, mùi thơm thanh mát. Múi cam to, tép mọng nước, vị chua dịu, thích hợp để vắt nước.",0,CURRENT_TIMESTAMP,0,93);
INSERT INTO RAUCU values (null,"Lê đường","trái cây",1,55000,"Lê đường là trái cây nhập khẩu Trung Quốc được nhiều người Việt Nam ưa chuộng. Lê đường hộp 1kg có vị ngọt đậm hơn lê đá, nhiều nước và thịt ít sạn cát hơn nên khi ăn cảm thấy mềm và xốp có màu vàng tươi, rất bắt mắt.",0,CURRENT_TIMESTAMP,0,54);
INSERT INTO RAUCU values (null,"Củ sắn","rau",1,3000,"Củ sắn là một loại củ khá ngọt, nhiều nước và rất giòn, chứa nhiều khoáng chất như: sắt, photpho, canxi và các vitamin A, C, B…. Thông thường người ta sẽ dùng củ sắn để ăn sống hoặc chế biến thành nhiều món ăn như: xào, luộc hay nấu canh,...",0,CURRENT_TIMESTAMP,0,33);
INSERT INTO RAUCU values (null,"Bông cải trắng","vietgap",1,12000,"Bông cải trắng hay còn gọi là súp lơ trắng là một loại rau họ cải, có giá trị dinh dưỡng rất cao. Bông cải trắng có phần bông màu trắng gắn khít vào nhau, xốp và hơi dai mềm, bên ngoài có lớp lá bao bọc xung quanh, phiến lá cứng và dày. Bông cải trắng tươi ngon, chất lượng.",0,CURRENT_TIMESTAMP,0,32);
INSERT INTO RAUCU values (null,"Khoai môn","vietgap",1,9000,"Củ khoai môn có hàm lượng chất xơ và nhiều loại vitamin, khoáng chất dồi dào rất cần thiết cho sức khỏe con người. Khoai môn có lớp vỏ ngoài màu nâu, phần thịt bên trong màu trắng kết hợp với nhiều đốm màu tím nhạt, có thể chế biến thành nhiều món ăn ngon, hấp dẫn.",0,CURRENT_TIMESTAMP,0,25);
